import { useState } from "react";
import axios from "axios";

export default function CreateUser() {
  const [formData, setFormData] = useState({
    fullName: "",
    username: "",
    password: "1",
    phone: "0987654321",
    role: "user",
    createdBy: 1, // thay bằng userID đang đăng nhập
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      await axios.post("https://your-api-url.com/user", formData);
      setMessage("✅ Tạo tài khoản thành công!");
      setFormData({
            fullName: "",
            username: "",
            password: "1",
            phone: "0987654321",
            role: "user",
            createdBy: 1, // thay bằng userID đang đăng nhập
      });
    } catch (error) {
      setMessage(error.response?.data || "❌ Lỗi tạo tài khoản");
    }

    setLoading(false);
  };

  const containerStyle = {
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#f0f2f5",
    padding: "20px",
  };

  const formStyle = {
    backgroundColor: "#fff",
    padding: "25px",
    borderRadius: "10px",
    maxWidth: "400px",
    width: "100%",
    boxShadow: "0 0 10px rgba(0,0,0,0.1)",
  };

  const inputStyle = {
    width: "100%",
    padding: "10px",
    marginTop: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    fontSize: "16px",
  };

  const buttonStyle = {
    width: "100%",
    padding: "12px",
    marginTop: "20px",
    backgroundColor: loading ? "#7aa7d9" : "#007bff",
    border: "none",
    color: "white",
    fontWeight: "bold",
    borderRadius: "5px",
    cursor: "pointer",
  };

  const titleStyle = {
    textAlign: "center",
    marginBottom: "20px",
  };

  const messageStyle = {
    marginTop: "10px",
    textAlign: "center",
    fontWeight: "bold",
  };

  return (
    <div style={containerStyle}>
      <form onSubmit={handleSubmit} style={formStyle}>
        <h2 style={titleStyle}>Tạo tài khoản</h2>


        <input
          style={inputStyle}
          type="text"
          name="fullName"
          placeholder="Họ và tên"
          value={formData.fullName}
          onChange={handleChange}
          required
        />

        <input
          style={inputStyle}
          type="text"
          name="username"
          placeholder="Tên đăng nhập"
          value={formData.username}
          onChange={handleChange}
          required
        />

        <input
          style={inputStyle}
          type="password"
          name="password"
          placeholder="Mật khẩu"
          value={formData.password}
          onChange={handleChange}
          required
          readOnly
        />

        <input
          style={inputStyle}
          type="text"
          name="phone"
          placeholder="Số điện thoại"
          value={formData.phone}
          onChange={handleChange}
          readOnly
        />

        <select
          name="role"
          value={formData.role}
          onChange={handleChange}
          style={inputStyle}
        >
          <option value="user">Người dùng</option>
          <option value="admin">Quản trị</option>
        </select>

        <button type="submit" disabled={loading} style={buttonStyle}>
          {loading ? "Đang tạo..." : "Tạo tài khoản"}
        </button>

        {message && <p style={messageStyle}>{message}</p>}
      </form>
    </div>
  );
}
