import logoSrc from '../img/logo.png';
import React, { useRef, useState } from "react";
import QRCode from "qrcode";
import jsPDF from "jspdf";

const StylishQR = () => {
  const canvasRef = useRef(null);
  const [text, setText] = useState('{"id":"TR001","type":"trash-bin","location":"Zone A"}');
  const [labelTop, setLabelTop] = useState("TR001");
  const [labelBottom, setLabelBottom] = useState("SCAN ME");

  const drawQRWithLabels = async () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const size = 500;
    const qrSize = 280;
    const padding = 40;
    const labelHeight = 50;

    canvas.width = size;
    canvas.height = size;
    ctx.clearRect(0, 0, size, size);

    // Nền tổng thể: đen
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, size, size);

    // ===== Vẽ Label Top với khung trắng =====
    ctx.fillStyle = "white";
    const topBoxWidth = size;
    const topBoxHeight = labelHeight;
    ctx.fillRect(0, 0, topBoxWidth, topBoxHeight);
    ctx.fillStyle = "black";
    ctx.fillRect(4, 4, topBoxWidth - 8, topBoxHeight - 8);

    ctx.fillStyle = "white";
    ctx.font = "bold 36px sans-serif";
    ctx.textAlign = "center";
    ctx.textBaseline = "middle";
    ctx.fillText(labelTop, size / 2, topBoxHeight / 2);

    // ===== Tạo QR Code =====
    const qr = await QRCode.create(text, { errorCorrectionLevel: "H" });
    const modules = qr.modules;
    const cellSize = qrSize / modules.size;

    const startX = (size - qrSize) / 2;
    const startY = topBoxHeight + 20;

    // Hộp nền trắng chứa QR
    const qrBoxRadius = 20;
    ctx.fillStyle = "white";
    ctx.beginPath();
    ctx.moveTo(startX + qrBoxRadius, startY);
    ctx.lineTo(startX + qrSize - qrBoxRadius, startY);
    ctx.quadraticCurveTo(startX + qrSize, startY, startX + qrSize, startY + qrBoxRadius);
    ctx.lineTo(startX + qrSize, startY + qrSize - qrBoxRadius);
    ctx.quadraticCurveTo(startX + qrSize, startY + qrSize, startX + qrSize - qrBoxRadius, startY + qrSize);
    ctx.lineTo(startX + qrBoxRadius, startY + qrSize);
    ctx.quadraticCurveTo(startX, startY + qrSize, startX, startY + qrSize - qrBoxRadius);
    ctx.lineTo(startX, startY + qrBoxRadius);
    ctx.quadraticCurveTo(startX, startY, startX + qrBoxRadius, startY);
    ctx.closePath();
    ctx.fill();

    // Vẽ mã QR (các ô đen)
    for (let r = 0; r < modules.size; r++) {
      for (let c = 0; c < modules.size; c++) {
        if (modules.get(r, c)) {
          const x = startX + c * cellSize;
          const y = startY + r * cellSize;
          ctx.fillStyle = "black";
          ctx.fillRect(x, y, cellSize, cellSize);
        }
      }
    }

    // Logo ở giữa
    const logoImg = new Image();
    logoImg.src = logoSrc;
    logoImg.onload = () => {
      const logoSize = qrSize * 0.28;
      const logoX = size / 2 - logoSize / 2;
      const logoY = startY + qrSize / 2 - logoSize / 2;

      ctx.fillStyle = "white";
      ctx.fillRect(logoX - 8, logoY - 8, logoSize + 16, logoSize + 16);
      ctx.drawImage(logoImg, logoX, logoY, logoSize, logoSize);

      // ===== Vẽ Label Bottom =====
      ctx.fillStyle = "white";
      const bottomBoxY = size - labelHeight;
      ctx.fillRect(0, bottomBoxY, size, labelHeight);
      ctx.fillStyle = "black";
      ctx.fillRect(4, bottomBoxY + 4, size - 8, labelHeight - 8);

      ctx.fillStyle = "white";
      ctx.font = "bold 32px sans-serif";
      ctx.textAlign = "center";
      ctx.textBaseline = "middle";
      ctx.fillText(labelBottom, size / 2, bottomBoxY + labelHeight / 2);
    };
  };

  const downloadPNG = () => {
    const canvas = canvasRef.current;
    const url = canvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = url;
    link.download = `${labelTop}_qrcode.png`;
    link.click();
  };

  const downloadPDF = () => {
    const canvas = canvasRef.current;
    const imageData = canvas.toDataURL("image/png");
    const pdf = new jsPDF({
      orientation: "portrait",
      unit: "pt",
      format: [canvas.width, canvas.height + 40],
    });

    pdf.addImage(imageData, "PNG", 0, 0, canvas.width, canvas.height);
    pdf.save(`${labelTop}_qrcode.pdf`);
  };

  return (
    <div>
      <h2 style={{ color: "white" }}>Tạo mã QR hiện đại</h2>
      <input value={text} onChange={(e) => setText(e.target.value)} placeholder="Nội dung mã QR" />
      <br />
      <input value={labelTop} onChange={(e) => setLabelTop(e.target.value)} placeholder="Label trên (VD: TR001)" />
      <br />
      <input value={labelBottom} onChange={(e) => setLabelBottom(e.target.value)} placeholder="Label dưới (VD: SCAN ME)" />
      <br />
      <button onClick={drawQRWithLabels} style={{ marginTop: 10 }}>Tạo mã QR</button>
      <button onClick={downloadPNG} style={{ marginTop: 10 }}>Tải PNG</button>
      <button onClick={downloadPDF} style={{ marginTop: 10 }}>Tải PDF</button>
      <br />
      <canvas ref={canvasRef} style={{ marginTop: 20, background: "#000", borderRadius: 10 }} />
    </div>
  );
};

export default StylishQR;
