import React, { useEffect, useRef, useState } from "react";
import QRCodeStyling from "qr-code-styling";
import logoImage from "../img/logo.png";

const ModernQR = () => {
  const qrRef = useRef(null);
  const canvasRef = useRef(null);
  const [qrCode, setQrCode] = useState(null);
  const [data, setData] = useState(
    '{"id":"TR001","type":"trash-bin","location":"Zone A", "t":"Rác sinh hoạt"}'
  );
  const [labelTop, setLabelTop] = useState("TR001");
  const [labelBottom, setLabelBottom] = useState("SCAN ME");

  useEffect(() => {
    const qr = new QRCodeStyling({
      width: 300,
      height: 300,
      data: encodeURIComponent(data),
      image: logoImage,
      qrOptions: {
        errorCorrectionLevel: "H", // Tăng mức độ phục hồi lỗi
      },
      dotsOptions: {
        color: "#00BFAE",  // Sử dụng màu sắc tươi sáng cho các điểm
        type: "rounded",   // Chọn dạng điểm bo tròn
      },
      cornersDotOptions: {
        type: "dot",
        color: "#FF5733",  // Tạo điểm bo tròn tại các góc với màu khác biệt
      },
      cornersSquareOptions: {
        type: "extra-rounded",  // Bo tròn các góc mã QR
        color: "#FF5733",
      },
      backgroundOptions: {
        color: "#FFF",  // Màu nền trắng cho mã QR
      },
      imageOptions: {
        crossOrigin: "anonymous",
        margin: 4,
        imageSize: 0.15, // Thêm logo với kích thước vừa phải
      },
    });

    setQrCode(qr);
  }, [data]);

  useEffect(() => {
    if (qrCode && qrRef.current) {
      qrRef.current.innerHTML = ""; // Xóa trước khi append mới
      qrCode.update({ data: encodeURIComponent(data), image: logoImage });
      qrCode.append(qrRef.current);
    }
  }, [qrCode, data]);

  const downloadImage = async () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const qrCanvas = document.querySelector("canvas");
    const qrImage = qrCanvas.toDataURL("image/png");

    const img = new Image();
    img.onload = () => {
      const width = 400;
      const height = 500;

      canvas.width = width;
      canvas.height = height;

      // Vẽ nền trắng
      ctx.fillStyle = "#fff";
      ctx.fillRect(10, 20, width - 20, height - 30);

      // Vẽ khung bo tròn
      ctx.strokeStyle = "#FF5733"; // Đổi màu viền
      ctx.lineWidth = 6;
      const radius = 30;
      const x = 10,
        y = 20,
        w = width - 20,
        h = height - 30;

      ctx.beginPath();
      ctx.moveTo(x + radius, y);
      ctx.lineTo(x + w - radius, y);
      ctx.quadraticCurveTo(x + w, y, x + w, y + radius);
      ctx.lineTo(x + w, y + h - radius);
      ctx.quadraticCurveTo(x + w, y + h, x + w - radius, y + h);
      ctx.lineTo(x + radius, y + h);
      ctx.quadraticCurveTo(x, y + h, x, y + h - radius);
      ctx.lineTo(x, y + radius);
      ctx.quadraticCurveTo(x, y, x + radius, y);
      ctx.closePath();
      ctx.stroke();

      // Label trên
      ctx.fillStyle = "#FF5733";
      ctx.font = "normal 40px sans-serif";
      ctx.textAlign = "center";
      ctx.fillText(labelTop, width / 2, 70);

      // Vẽ QR ở giữa
      ctx.drawImage(img, 50, 100, 300, 300);

      // Label dưới
      ctx.fillStyle = "#FF5733";  // Đổi màu chữ cho label dưới
      ctx.font = "normal 28px sans-serif";
      ctx.textAlign = "center";
      ctx.fillText(labelBottom, width / 2, 460);

      // Tải về
      const link = document.createElement("a");
      link.download = `${labelTop}_qr.png`;
      link.href = canvas.toDataURL("image/png");
      link.click();
    };
    img.src = qrImage;
  };

  return (
    <div style={{ backgroundColor: "#fff", padding: 20, textAlign: "center" }}>
      <div style={{ color: "black", fontSize: "36px", marginBottom: 10 }}>
        {labelTop}
      </div>
      <div ref={qrRef} style={{ margin: "0 auto" }}></div>
      <div style={{ color: "black", fontSize: "28px", marginTop: 10 }}>
        {labelBottom}
      </div>

      <div style={{ marginTop: 20 }}>
        <input
          value={data}
          onChange={(e) => setData(e.target.value)}
          placeholder="Nội dung QR"
          style={{ width: "80%" }}
        />
        <br />
        <input
          value={labelTop}
          onChange={(e) => setLabelTop(e.target.value)}
          placeholder="Label trên"
        />
        <input
          value={labelBottom}
          onChange={(e) => setLabelBottom(e.target.value)}
          placeholder="Label dưới"
        />
        <br />
        <button onClick={downloadImage} style={{ marginTop: 10 }}>
          Tải QR PNG
        </button>
        <canvas ref={canvasRef} style={{ display: "none" }}></canvas>
      </div>
    </div>
  );
};

export default ModernQR;
