import React, { useEffect, useRef, useState } from "react";
import QRCodeStyling from "qr-code-styling";
import logoImage from "../img/logo.png";

const ModernQR = () => {
  const qrRef = useRef(null);
  const canvasRef = useRef(null);
  const [qrCode, setQrCode] = useState(null);
  const [data, setData] = useState('{"id":"TR001","type":"trash-bin","location":"Zone A"}');
  const [labelTop, setLabelTop] = useState("TR001");
  const [labelBottom, setLabelBottom] = useState("SCAN ME");

  useEffect(() => {
    const qr = new QRCodeStyling({
      width: 300,
      height: 300,
      data,
      image: logoImage,
      dotsOptions: {
        color: "#000000",
        type: 'rounded',
      },
      cornersDotOptions: {
        type: "dot",
        color: "#000000"
      },
      cornersSquareOptions: {
        type: "extra-rounded",
        color: "#000000"
      },
      backgroundOptions: {
        color: "#FFFFFF",
      },
      imageOptions: {
        crossOrigin: "anonymous",
        margin: 4,
        imageSize: 0.2,
      }
    });

    setQrCode(qr);
  }, []);

  useEffect(() => {
    if (qrCode && qrRef.current) {
      qrCode.update({ data, image: logoImage });
      qrCode.append(qrRef.current);
    }
  }, [qrCode, data]);

  const downloadImage = async () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const qrCanvas = document.querySelector("canvas");
    const qrImage = qrCanvas.toDataURL("image/png");

    const img = new Image();
    img.onload = () => {
      const width = 400;
      const height = 500;

      canvas.width = width;
      canvas.height = height;

      // Vẽ nền đen
      ctx.fillStyle = "#fff";
      ctx.fillRect(0, 0, width, height);

      // Vẽ khung bo tròn
      ctx.strokeStyle = "#000";
      ctx.lineWidth = 4;
      const radius = 20;
      const x = 10, y = 20, w = width - 20, h = height - 30;

      ctx.beginPath();
      ctx.moveTo(x + radius, y);
      ctx.lineTo(x + w - radius, y);
      ctx.quadraticCurveTo(x + w, y, x + w, y + radius);
      ctx.lineTo(x + w, y + h - radius);
      ctx.quadraticCurveTo(x + w, y + h, x + w - radius, y + h);
      ctx.lineTo(x + radius, y + h);
      ctx.quadraticCurveTo(x, y + h, x, y + h - radius);
      ctx.lineTo(x, y + radius);
      ctx.quadraticCurveTo(x, y, x + radius, y);
      ctx.closePath();
      ctx.stroke();

      // Label trên
      ctx.fillStyle = "black";
      ctx.font = "normal 36px sans-serif";
      ctx.textAlign = "center";
      ctx.fillText(labelTop, width / 2, 70);

      // 🔹 Đường kẻ dưới labelTop
      ctx.beginPath();
      ctx.moveTo(20, 80); // x1, y1
      ctx.lineTo(width - 20, 80); // x2, y2
      ctx.lineWidth = 2;
      ctx.strokeStyle = "#ccc";
      ctx.stroke();

      // Vẽ QR code ở giữa
      ctx.drawImage(img, 50, 100, 300, 300);

      // 🔹 Đường kẻ trên labelBottom
      ctx.beginPath();
      ctx.moveTo(20, 420);
      ctx.lineTo(width - 20, 420);
      ctx.lineWidth = 2;
      ctx.strokeStyle = "#ccc";
      ctx.stroke();


      // Label dưới
      ctx.font = "normal 28px sans-serif";
      ctx.fillText(labelBottom, width / 2, 460);

      // Tải ảnh
      const link = document.createElement("a");
      link.download = `${labelTop}_qr.png`;
      link.href = canvas.toDataURL("image/png");
      link.click();
    };
    img.src = qrImage;
  };

  return (
    <div style={{ backgroundColor: "#fff", padding: 20, textAlign: "center" }}>
      <div style={{ color: "black", fontSize: "36px", fontWeight: 'normal', marginBottom: 10 }}>
        {labelTop}
      </div>
      <div ref={qrRef} style={{ margin: "0 auto" }}></div>
      <div style={{ color: "black", fontSize: "28px", fontWeight: "normal", marginTop: 10 }}>
        {labelBottom}
      </div>

      <div style={{ marginTop: 20 }}>
        <input value={data} onChange={(e) => setData(e.target.value)} placeholder="Nội dung QR" style={{ width: "80%" }} />
        <br />
        <input value={labelTop} onChange={(e) => setLabelTop(e.target.value)} placeholder="Label trên" />
        <input value={labelBottom} onChange={(e) => setLabelBottom(e.target.value)} placeholder="Label dưới" />
        <br />
        <button onClick={downloadImage} style={{ marginTop: 10 }}>Tải QR PNG</button>
        <canvas ref={canvasRef} style={{ display: "none" }}></canvas>
      </div>
    </div>
  );
};

export default ModernQR;
