        },
        {
          code: '13b',
          name: 'Chuyền 13B',
        },
        {
          code: '14b',
          name: 'Chuyền 14B',
        },
      ]
    },
    {
      code: 'robot',
      name: 'Robot',
      chuyen: [
        {
          code: 'rb1',
          name: 'Chuyền RB1',
        },
        {
          code: 'rb2',
          name: 'Chuyền RB2',
        },
        {
          code: 'rb3',
          name: 'Chuyền RB3',
        },
      ]
    },
    {
      code: 'mau',
      name: 'Mẫu',
      chuyen: [
        {
          code: '1b2a',
          name: 'Chuyền 1B-2A',
        }
      ]
    },
    {
      code: 'phamau',
      name: 'Pha màu',
      chuyen: [],
    },
    {
      code: 'chupkhuon',
      name: 'Chụp khuôn',
      chuyen: [],
    },
    {
      code: 'gnkh',
      name: 'GN-KH',
      chuyen: [],
    },
    {
      code: 'logo',
      name: 'Logo',
      chuyen: [],
    },
    {
      code: 'canhhang',
      name: 'Canh hàng',
      chuyen: [],
    },
    {
      code: 'chuikhuon',
      name: 'Chùi khuôn',
      chuyen: [],
    },
    {
      code: 'pbh',
      name: 'PBH',
      chuyen: [],
    },
    {
      code: 'pcl',
      name: 'PCL',
      chuyen: [],
    },
    {
      code: 'pcs',
      name: 'PCS',
      chuyen: [],
    },
    {
