      chuyen: [],
    },
    {
      code: 'ep',
      name: 'Ép',
      chuyen: [],
    },
    {
      code: '16can',
      name: '16 căn',
      chuyen: [],
    },
  ];

  const caLam = [
    {
      code: 'ca1',
      name: 'Ca 1',
    },
    {
      code: 'ca2',
      name: 'Ca 2',
    },
    {
      code: 'ca3',
      name: 'Ca 3',
    },
    {
      code: 'dai1',
      name: 'Dài 1',
    },
    {
      code: 'dai2',
      name: 'Dài 2',
    },
    {
      code: 'hanhchinh',
      name: 'Hành chính',
    }
  ]

  const loaiRac = [
    {
      code: 'bangkeo',
      name: 'Băng keo',
    },
    {
      code: 'vaichuikhuon',
      name: 'Vải chùi khuôn',
    },
    {
      code: 'vaidinhmuc',
      name: 'Vải dính mực',
    },
    {
      code: 'vaidinhlapa',
      name: 'Vải dính lapa',
    },
    {
      code: 'keochuiban',
      name: 'Keo chùi bàn',
    },
    {
      code: 'mucin',
      name: 'Mực in',
    },
    {
      code: 'lapa',
      name: 'Lapa',
    },
    {
      code: 'racsinhhoat',
      name: 'Rác sinh hoạt',
    },
  ]

  const [indexData, setIndexData] = useState('to');
  const [vtChuyen, setVtChuyen] = useState(0);

  const [dataTo, setDataTo] = useState('');
  const [dataChuyen, setDataChuyen] = useState('');
  const [dataCa, setDataCa] = useState('');
  const [dataRac, setDataRac] = useState('');

  const [qrCode, setQrCode] = useState('');

  const handleTo = (vt, to) => {
    setIndexData(bpTo[vt].chuyen.length > 0 ? 'chuyen' : 'ca');
    setVtChuyen(vt);
    setDataTo(to);
  }

  const handleChuyen = (chuyen) => {
    setIndexData('ca');
    setDataChuyen(chuyen);
  }

  const handleCalam = (ca) => {
    setIndexData('rac');
    setDataCa(ca);
  }

  const handleRac = (rac) => {
    setIndexData('qr');
    setDataRac(rac);
  }

  const findCodeQr = () => {
    const url = dataTo + '/' + dataChuyen + '/' + dataCa + '/' + dataRac;

    GenerateQRCode(url);
  }

  const GenerateQRCode = (url) => {
    QRCode.toDataURL(url, (err, url) => {
      if (err) return console.error(err)

      console.log(url);
      setQrCode(url);
    })
  }

  return (
    <div className="App">
      <div className='data-main'>
        <div onClick={() => {
          setIndexData('to');
          setDataTo('');
          setDataChuyen('');
          setDataCa('');
          setDataRac('');
          setQrCode('');
        }} className='list-main'>{dataTo}</div>
        {
          dataChuyen && <span>{` - `}</span>
        }
        <div onClick={() => {
          setIndexData('chuyen');
          setDataChuyen('');
          setDataCa('');
          setDataRac('');
          setQrCode('');
        }} className='list-main'>{dataChuyen}</div>
        {
          dataCa && <span>{` - `}</span>
        }
        <div onClick={() => {
          setIndexData('ca');
          setDataCa('');
          setDataRac('');
        }} className='list-main'>{dataCa}</div>
        {
          dataRac && <span>{` - `}</span>
        }
        <div onClick={() => {
          setIndexData('rac');
          setDataRac('');
        }} className='list-main'>{dataRac}</div>
      </div>

      {
        indexData === 'qr' &&
        <button onClick={() => findCodeQr()} className='btn'>Lấy mã QR</button>
      }

      <img src={qrCode} />

      {
        indexData === 'to' &&
        <div className='wrapper-to'>
          <h3 className='title'>Chọn tổ / bộ phận</h3>
          <ul className='list-bp'>
            {
              bpTo.map((e, i) => {
                return <li onClick={() => handleTo(i, e.name)} className='item-bp' key={i}>{e.name}</li>
              })
            }
          </ul>
        </div>
      }

      {
        indexData === 'chuyen' &&
        <div className='wrapper-to'>
          <h3 className='title'>Chọn chuyền / máy</h3>
          <ul className='list-bp'>
            {
              bpTo[vtChuyen].chuyen.map((e, i) => {
                return <li onClick={() => handleChuyen(e.name)} className='item-bp' key={i}>{e.name}</li>
              })
            }
          </ul>
        </div>
      }

      {
        indexData === 'ca' &&
        <div className='wrapper-to'>
          <h3 className='title'>Chọn ca làm</h3>
          <ul className='list-bp'>
            {
              caLam.map((e, i) => {
                return <li onClick={() => handleCalam(e.name)} className='item-bp' key={i}>{e.name}</li>
              })
            }
          </ul>
        </div>
      }


      {
        indexData === 'rac' &&
        <div className='wrapper-to'>
          <h3 className='title'>Chọn loại rác</h3>
          <ul className='list-bp'>
            {
              loaiRac.map((e, i) => {
                return <li onClick={() => handleRac(e.name)} className='item-bp' key={i}>{e.name}</li>
              })
            }
          </ul>
        </div>
      }

    </div>
  );
}

export default App;
