import React, { useState, useRef, useEffect } from "react";
import { QRCodeCanvas } from "qrcode.react";
import logoSrc from '../img/logo.png';

const Home = () => {
  const [text, setText] = useState("Hello World!");
  const canvasRef = useRef(null);

  const drawLogoOnCanvas = (canvas, logoImg) => {
    const ctx = canvas.getContext("2d");
    const size = canvas.width;
    const logoSize = size * 0.2;

    ctx.save();

    // Vẽ nền trắng cho logo (bo góc)
    const centerX = (size - logoSize) / 2;
    const centerY = (size - logoSize) / 2;
    const radius = 10;

    ctx.fillStyle = "white";
    ctx.beginPath();
    ctx.moveTo(centerX + radius, centerY);
    ctx.lineTo(centerX + logoSize - radius, centerY);
    ctx.quadraticCurveTo(centerX + logoSize, centerY, centerX + logoSize, centerY + radius);
    ctx.lineTo(centerX + logoSize, centerY + logoSize - radius);
    ctx.quadraticCurveTo(centerX + logoSize, centerY + logoSize, centerX + logoSize - radius, centerY + logoSize);
    ctx.lineTo(centerX + radius, centerY + logoSize);
    ctx.quadraticCurveTo(centerX, centerY + logoSize, centerX, centerY + logoSize - radius);
    ctx.lineTo(centerX, centerY + radius);
    ctx.quadraticCurveTo(centerX, centerY, centerX + radius, centerY);
    ctx.closePath();
    ctx.fill();

    // Vẽ logo
    ctx.drawImage(logoImg, centerX, centerY, logoSize, logoSize);

    ctx.restore();
  };

  const downloadQRCode = () => {
    const canvas = canvasRef.current.querySelector("canvas");
    const logoImg = new Image();
    logoImg.src = logoSrc;
    logoImg.onload = () => {
      drawLogoOnCanvas(canvas, logoImg);
      const url = canvas.toDataURL("image/png");
      const link = document.createElement("a");
      link.href = url;
      link.download = "qr-code-with-logo.png";
      link.click();
    };
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Tạo và Tải mã QR có logo</h2>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Nhập chuỗi..."
        style={{ marginBottom: 20, padding: 8, width: "300px" }}
      />

      {/* QR Code được render trên canvas */}
      <div ref={canvasRef}>
        <QRCodeCanvas value={text} size={256} includeMargin={true} />
      </div>

      <button onClick={downloadQRCode} style={{ marginTop: 20, padding: "10px 20px" }}>
        Tải mã QR có logo
      </button>
    </div>
  );
};

export default QRGenerator;
