import React, { useEffect, useRef, useState } from "react";
import QRCodeStyling from "qr-code-styling";
import logoImage from "../img/logo.png";

const ModernQR = () => {
  const qrRef = useRef(null);
  const [qrCode, setQrCode] = useState(null);
  const [data, setData] = useState('{"id":"TR001","type":"trash-bin","location":"Zone A"}');
  const [labelTop, setLabelTop] = useState("TR001");
  const [labelBottom, setLabelBottom] = useState("SCAN ME");

  useEffect(() => {
    const qr = new QRCodeStyling({
      width: 300,
      height: 300,
      data,
      image: logoImage,
      dotsOptions: {
        color: "#000000",
        type: "dots", // Bo góc
      },
      cornersDotOptions: {
        type: "dot", // Dấu định vị bo tròn
        color: "#000000"
      },
      cornersSquareOptions: {
        type: "extra-rounded", // Góc bo tròn
        color: "#000000"
      },
      backgroundOptions: {
        color: "#FFFFFF",
      },
      imageOptions: {
        crossOrigin: "anonymous",
        margin: 4,
        imageSize: 0.2,
      }
    });

    setQrCode(qr);
  }, []);

  useEffect(() => {
    if (qrCode && qrRef.current) {
      qrCode.update({
        data,
        image: logoImage,
      });
      qrCode.append(qrRef.current);
    }
  }, [qrCode, data]);

  const downloadImage = () => {
    qrCode.download({ name: `${labelTop}_qr`, extension: "png" });
  };

  return (
    <div style={{ backgroundColor: "#000", padding: 20, textAlign: "center" }}>
      <div style={{ color: "white", fontSize: "36px", fontWeight: "bold", marginBottom: 10 }}>
        {labelTop}
      </div>
      <div ref={qrRef} style={{ margin: "0 auto" }}></div>
      <div style={{ color: "white", fontSize: "28px", fontWeight: "bold", marginTop: 10 }}>
        {labelBottom}
      </div>

      <div style={{ marginTop: 20 }}>
        <input value={data} onChange={(e) => setData(e.target.value)} placeholder="Nội dung QR" style={{ width: "80%" }} />
        <br />
        <input value={labelTop} onChange={(e) => setLabelTop(e.target.value)} placeholder="Label trên" />
        <input value={labelBottom} onChange={(e) => setLabelBottom(e.target.value)} placeholder="Label dưới" />
        <br />
        <button onClick={downloadImage} style={{ marginTop: 10 }}>Tải QR PNG</button>
      </div>
    </div>
  );
};

export default ModernQR;
