import logoSrc from '../img/logo.png';
import React, { useRef, useState } from "react";
import QRCode from "qrcode";
import jsPDF from "jspdf";

const StylishQR = () => {
  const canvasRef = useRef(null);
  const [text, setText] = useState("https://example.com");
  const [labelTop, setLabelTop] = useState("TR001");
  const [labelBottom, setLabelBottom] = useState("SCAN ME");
  const logoSrc = "/logo.png"; // Đảm bảo bạn đã có logo.png trong thư mục public/

  const drawQRWithLabels = async () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const size = 400; // Kích thước toàn bộ ảnh
    const qrSize = 260; // Kích thước phần QR code
    const margin = 20;

    canvas.width = size;
    canvas.height = size;
    ctx.clearRect(0, 0, size, size);

    // Nền đen
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, size, size);

    // Label trên
    ctx.fillStyle = "white";
    ctx.font = "32px sans-serif";
    ctx.textAlign = "center";
    ctx.fillText(labelTop, size / 2, 50);

    // Tạo QR code data
    const qr = await QRCode.create(text, { errorCorrectionLevel: "H" });
    const modules = qr.modules;
    const cellSize = qrSize / modules.size;
    const startX = (size - qrSize) / 2;
    const startY = 70;

    // Vẽ các ô QR hình tròn
    for (let r = 0; r < modules.size; r++) {
      for (let c = 0; c < modules.size; c++) {
        if (modules.get(r, c)) {
          const x = startX + c * cellSize + cellSize / 2;
          const y = startY + r * cellSize + cellSize / 2;
          const radius = cellSize * 0.4;

          ctx.beginPath();
          ctx.arc(x, y, radius, 0, 2 * Math.PI);
          ctx.fillStyle = "white";
          ctx.fill();
        }
      }
    }

    // Vẽ logo ở giữa
    const logoImg = new Image();
    logoImg.src = logoSrc;
    logoImg.onload = () => {
      const logoSize = qrSize * 0.25;
      const logoX = size / 2 - logoSize / 2;
      const logoY = startY + qrSize / 2 - logoSize / 2;

      // Nền trắng cho logo
      ctx.fillStyle = "white";
      ctx.fillRect(logoX - 5, logoY - 5, logoSize + 10, logoSize + 10);

      // Logo
      ctx.drawImage(logoImg, logoX, logoY, logoSize, logoSize);

      // Label dưới
      ctx.fillStyle = "white";
      ctx.font = "28px sans-serif";
      ctx.textAlign = "center";
      ctx.fillText(labelBottom, size / 2, size - 20);
    };
  };

  const downloadPNG = () => {
    const canvas = canvasRef.current;
    const url = canvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = url;
    link.download = `${labelTop}_qrcode.png`;
    link.click();
  };

  const downloadPDF = () => {
    const canvas = canvasRef.current;
    const imageData = canvas.toDataURL("image/png");

    const pdf = new jsPDF({
      orientation: "portrait",
      unit: "pt",
      format: [canvas.width, canvas.height + 40],
    });

    pdf.addImage(imageData, "PNG", 0, 0, canvas.width, canvas.height);
    pdf.save(`${labelTop}_qrcode.pdf`);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Tạo mã QR hiện đại</h2>
      <input
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Nội dung mã QR"
        style={{ padding: 8, width: 300 }}
      />
      <br />
      <input
        value={labelTop}
        onChange={(e) => setLabelTop(e.target.value)}
        placeholder="Label trên (VD: TR001)"
        style={{ padding: 8, width: 300, marginTop: 10 }}
      />
      <br />
      <input
        value={labelBottom}
        onChange={(e) => setLabelBottom(e.target.value)}
        placeholder="Label dưới (VD: SCAN ME)"
        style={{ padding: 8, width: 300, marginTop: 10 }}
      />
      <br />
      <button onClick={drawQRWithLabels} style={{ marginTop: 10, marginRight: 10 }}>
        Tạo mã QR
      </button>
      <button onClick={downloadPNG} style={{ marginRight: 10 }}>
        Tải PNG
      </button>
      <button onClick={downloadPDF}>Tải PDF</button>
      <br />
      <canvas ref={canvasRef} style={{ marginTop: 20, background: "#000" }} />
    </div>
  );
};

export default StylishQR;
