import React, { useState, useRef } from "react";
import QRCode from "qrcode";

const QRWithLogo = () => {
  const [text, setText] = useState("Hello World!");
  const canvasRef = useRef(null);
  const logoSrc = "/logo.png"; // logo đặt trong public/

  const generateQRWithLogo = async () => {
    const canvas = canvasRef.current;

    // 1. Tạo QR code lên canvas
    await QRCode.toCanvas(canvas, text, {
      width: 300,
      margin: 2,
    });

    // 2. Load logo
    const logoImg = new Image();
    logoImg.src = logoSrc;
    logoImg.onload = () => {
      const ctx = canvas.getContext("2d");
      const logoSize = 60;
      const x = (canvas.width - logoSize) / 2;
      const y = (canvas.height - logoSize) / 2;

      // Vẽ nền trắng bo góc (tùy chọn)
      ctx.fillStyle = "white";
      ctx.fillRect(x, y, logoSize, logoSize);

      // Vẽ logo
      ctx.drawImage(logoImg, x, y, logoSize, logoSize);
    };
  };

  const downloadQR = () => {
    const canvas = canvasRef.current;
    const url = canvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = url;
    link.download = "qr-with-logo.png";
    link.click();
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Tạo mã QR có logo</h2>
      <input
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Nhập nội dung"
        style={{ padding: 8, width: 300 }}
      />
      <br />
      <button onClick={generateQRWithLogo} style={{ margin: "10px" }}>
        Tạo mã QR
      </button>
      <button onClick={downloadQR}>Tải xuống</button>
      <br />
      <canvas ref={canvasRef} width={300} height={300} style={{ marginTop: 20 }} />
    </div>
  );
};

export default QRWithLogo;
