import React, { useState, useRef } from "react";
import { QRCodeCanvas } from "qrcode.react";
import logo from '../img/logo.png';

const Home = () => {
  const [text, setText] = useState("Hello World!");
  const qrRef = useRef(null);

  const downloadQRCode = () => {
    const canvas = qrRef.current.querySelector("canvas");
    const url = canvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = url;
    link.download = "qr-code.png";
    link.click();
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Tạo mã QR có logo</h2>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Nhập chuỗi..."
        style={{ marginBottom: 20, padding: 8, width: "300px" }}
      />

      {/* QR Code + Logo */}
      <div
        ref={qrRef}
        style={{
          position: "relative",
          width: 256,
          height: 256,
          marginBottom: 20,
        }}
      >
        <QRCodeCanvas value={text} size={256} includeMargin={true} />
        <img
          src={logo} // Đường dẫn đến logo (có thể là URL hoặc import)
          alt="logo"
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: 64,
            height: 84,
            borderRadius: 10,
            backgroundColor: "white",
            padding: 4,
          }}
        />
      </div>

      <button onClick={downloadQRCode} style={{ padding: "10px 20px" }}>
        Tải mã QR xuống
      </button>
    </div>
  );
};

export default Home;
